    k <- (i-1)*length(cols) + j
    m <- length(x[i,])
    n <- length(y[k,])
    r <- sum(rank(c(x[i,],y[k,]))[seq_along(x[i,])])
    r1 <- sum(rank(c(x1[i,],y[k,]))[seq_along(x1[i,])])
    effect_size[i, j] <- (r/m - (m + 1)/2)/n
    effect_size1[i, j] <- (r1/m - (m + 1)/2)/n
    statistical_diff[i, j] <- wilcox.test(x[i,], y[k,])$p.value
  }
}

effect_size
effect_size1
statistical_diff
